    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { insertRoomSchema, insertPlayerSchema, chatMessageSchema, playerSchema, roomSchema } from "@shared/schema";
import { z } from "zod";

interface ExtendedWebSocket extends WebSocket {
  playerId?: string;
  roomId?: string;
}

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  // Store WebSocket connections
  const connections = new Map<string, ExtendedWebSocket>();
  const roomConnections = new Map<string, Set<string>>();

  // Setup automatic room cleanup every 10 minutes
  setInterval(async () => {
    const cleanedCount = await storage.cleanupExpiredRooms();
    if (cleanedCount > 0) {
      console.log(`Cleaned up ${cleanedCount} expired rooms`);
    }
  }, 10 * 60 * 1000); // 10 minutes

  // Broadcast to all clients in a room
  function broadcastToRoom(roomId: string, message: any, excludeId?: string) {
    const roomClients = roomConnections.get(roomId);
    if (!roomClients) return;

    roomClients.forEach(clientId => {
      if (excludeId && clientId === excludeId) return;
      const ws = connections.get(clientId);
      if (ws && ws.readyState === WebSocket.OPEN) {
        ws.send(JSON.stringify(message));
      }
    });
  }

  // WebSocket connection handling
  wss.on('connection', (ws: ExtendedWebSocket) => {
    const connectionId = Math.random().toString(36).substring(7);
    connections.set(connectionId, ws);

    ws.on('message', async (data: Buffer) => {
      try {
        const message = JSON.parse(data.toString());

        switch (message.type) {
          case 'createRoom':
            try {
              const { hostName, maxPlayers = 8 } = message;
              const room = await storage.createRoom({ hostId: '', maxPlayers: maxPlayers });

              // Add host as first player (without color initially)
              const host = await storage.addPlayerToRoom(room.id, {
                name: hostName,
                color: '', // Will be set later in lobby
                avatar: hostName.charAt(0).toUpperCase()
              });

              if (host) {
                // Update room with correct host ID
                await storage.updateRoom(room.id, { hostId: host.id });

                // Set WebSocket properties
                ws.roomId = room.id;
                ws.playerId = host.id;

                // Add to room connections
                if (!roomConnections.has(room.id)) {
                  roomConnections.set(room.id, new Set());
                }
                roomConnections.get(room.id)!.add(connectionId);

                const updatedRoom = await storage.getRoom(room.id);
                ws.send(JSON.stringify({
                  type: 'roomCreated',
                  room: updatedRoom,
                  playerId: host.id
                }));
              }
            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to create room'
              }));
            }
            break;

          case 'joinRoom':
            try {
              const { roomCode, playerName } = message;
              const room = await storage.getRoomByCode(roomCode);

              if (!room) {
                ws.send(JSON.stringify({
                  type: 'error',
                  message: 'Room not found'
                }));
                break;
              }

              if (room.players.length >= room.maxPlayers) {
                ws.send(JSON.stringify({
                  type: 'error',
                  message: 'Room is full'
                }));
                break;
              }

              if (room.gameState !== 'waiting') {
                ws.send(JSON.stringify({
                  type: 'error',
                  message: 'Game already in progress'
                }));
                break;
              }

              const player = await storage.addPlayerToRoom(room.id, {
                name: playerName,
                color: '', // Will be set later in lobby
                avatar: playerName.charAt(0).toUpperCase()
              });

              if (player) {
                ws.roomId = room.id;
                ws.playerId = player.id;

                // Add to room connections
                if (!roomConnections.has(room.id)) {
                  roomConnections.set(room.id, new Set());
                }
                roomConnections.get(room.id)!.add(connectionId);

                const updatedRoom = await storage.getRoom(room.id);

                // Send success to joining player
                ws.send(JSON.stringify({
                  type: 'roomJoined',
                  room: updatedRoom,
                  playerId: player.id
                }));

                // Broadcast player joined to room
                broadcastToRoom(room.id, {
                  type: 'playerJoined',
                  room: updatedRoom,
                  player
                }, connectionId);
              }
            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to join room'
              }));
            }
            break;

          case 'rejoinRoom':
            try {
              const { roomCode, playerId } = message;
              const room = await storage.getRoomByCode(roomCode);

              if (!room) {
                ws.send(JSON.stringify({
                  type: 'error',
                  message: 'Room not found'
                }));
                break;
              }

              // Check if player exists in room
              const existingPlayer = room.players.find(p => p.id === playerId);
              if (!existingPlayer) {
                ws.send(JSON.stringify({
                  type: 'error',
                  message: 'Player not found in room'
                }));
                break;
              }

              // Update player connection status
              await storage.updatePlayer(room.id, playerId, { isConnected: true });

              // Set WebSocket properties
              ws.roomId = room.id;
              ws.playerId = playerId;

              // Add to room connections
              if (!roomConnections.has(room.id)) {
                roomConnections.set(room.id, new Set());
              }
              roomConnections.get(room.id)!.add(connectionId);

              const updatedRoom = await storage.getRoom(room.id);

              // Send room data to rejoining player
              ws.send(JSON.stringify({
                type: 'roomJoined',
                room: updatedRoom,
                playerId: playerId
              }));

              // Broadcast player reconnected to room
              broadcastToRoom(room.id, {
                type: 'playerJoined',
                room: updatedRoom,
                player: existingPlayer
              }, connectionId);

            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to rejoin room'
              }));
            }
            break;

          case 'changeColor':
            try {
              const { playerId, color } = message;
              if (!ws.roomId || !ws.playerId) break;

              // Check if color is already taken
              const room = await storage.getRoom(ws.roomId);
              if (room) {
                const colorTaken = room.players.some(p => p.id !== playerId && p.color === color);
                if (colorTaken) {
                  ws.send(JSON.stringify({
                    type: 'error',
                    message: 'Color already taken by another player'
                  }));
                  break;
                }

                // Update player color
                await storage.updatePlayer(ws.roomId, playerId, { color });

                const updatedRoom = await storage.getRoom(ws.roomId);
                broadcastToRoom(ws.roomId, {
                  type: 'playerColorChanged',
                  room: updatedRoom,
                  playerId
                });
              }
            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to change color'
              }));
            }
            break;

          case 'kickPlayer':
            try {
              const { hostId, playerToKickId } = message;
              const room = await storage.getRoom(ws.roomId!);

              if (!room || room.hostId !== hostId) {
                ws.send(JSON.stringify({
                  type: 'error',
                  message: 'Only host can kick players'
                }));
                break;
              }

              // Find the kicked player's connection
              const kickedPlayerConnection = Array.from(connections.entries()).find(
                ([_, ws]) => ws.playerId === playerToKickId && ws.roomId === room.id
              );

              if (kickedPlayerConnection) {
                const [kickedConnectionId, kickedWs] = kickedPlayerConnection;

                // Send kick message to kicked player
                kickedWs.send(JSON.stringify({
                  type: 'kicked',
                  message: 'You have been kicked from the room'
                }));

                // Remove from connections
                connections.delete(kickedConnectionId);
                const roomClients = roomConnections.get(room.id);
                if (roomClients) {
                  roomClients.delete(kickedConnectionId);
                }
              }

              // Remove player from room
              await storage.removePlayer(ws.roomId!, playerToKickId);

              const updatedRoom = await storage.getRoom(ws.roomId!);
              broadcastToRoom(ws.roomId!, {
                type: 'playerKicked',
                room: updatedRoom,
                kickedPlayerId: playerToKickId
              });

            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to kick player'
              }));
            }
            break;

          case 'playerReady':
            try {
              const { playerId, isReady } = message;
              if (!ws.roomId || !ws.playerId) break;

              await storage.updatePlayer(ws.roomId, ws.playerId, { isReady });
              const room = await storage.getRoom(ws.roomId);

              broadcastToRoom(ws.roomId, {
                type: 'playerReadyChanged',
                room,
                playerId: ws.playerId,
                isReady
              });
            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to update ready status'
              }));
            }
            break;

          case 'startGame':
            try {
              if (!ws.roomId) break;

              const room = await storage.getRoom(ws.roomId);
              if (!room || room.hostId !== ws.playerId) {
                ws.send(JSON.stringify({
                  type: 'error',
                  message: 'Only host can start the game'
                }));
                break;
              }

              // Check if all players are ready
              const allReady = room.players.every(p => p.isReady);
              if (!allReady || room.players.length < 2) {
                ws.send(JSON.stringify({
                  type: 'error',
                  message: 'All players must be ready and minimum 2 players required'
                }));
                break;
              }

              await storage.startGame(ws.roomId);
              const updatedRoom = await storage.getRoom(ws.roomId);

              broadcastToRoom(ws.roomId, {
                type: 'gameStarted',
                room: updatedRoom
              });
            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to start game'
              }));
            }
            break;

          case 'rollDice':
            try {
              if (!ws.roomId || !ws.playerId) break;

              const room = await storage.getRoom(ws.roomId);
              if (!room || room.gameState !== 'playing') break;

              const currentPlayer = room.players[room.currentPlayerIndex];
              if (currentPlayer.id !== ws.playerId) {
                ws.send(JSON.stringify({
                  type: 'error',
                  message: 'Not your turn'
                }));
                break;
              }

              // Generate dice roll
              const dice1 = Math.floor(Math.random() * 6) + 1;
              const dice2 = Math.floor(Math.random() * 6) + 1;
              const total = dice1 + dice2;

              // Calculate new position
              let newPosition = (currentPlayer.position + total) % 40;

              // Check if passed START (collect ₹2000)
              let passedStart = false;
              if (currentPlayer.position + total >= 40) {
                passedStart = true;
                await storage.updatePlayer(ws.roomId, ws.playerId, { 
                  money: currentPlayer.money + 2000 
                });
              }

              // Update player position
              await storage.updatePlayer(ws.roomId, ws.playerId, { 
                position: newPosition 
              });

              const updatedRoom = await storage.getRoom(ws.roomId);

              broadcastToRoom(ws.roomId, {
                type: 'diceRolled',
                room: updatedRoom,
                playerId: ws.playerId,
                dice1,
                dice2,
                total,
                newPosition,
                passedStart
              });

            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to roll dice'
              }));
            }
            break;

          case 'endTurn':
            try {
              if (!ws.roomId || !ws.playerId) break;

              const room = await storage.getRoom(ws.roomId);
              if (!room || room.gameState !== 'playing') break;

              const currentPlayer = room.players[room.currentPlayerIndex];
              if (currentPlayer.id !== ws.playerId) break;

              // Move to next player
              const nextPlayerIndex = (room.currentPlayerIndex + 1) % room.players.length;
              await storage.updateRoom(ws.roomId, { 
                currentPlayerIndex: nextPlayerIndex,
                turnNumber: room.turnNumber + 1
              });

              const updatedRoom = await storage.getRoom(ws.roomId);

              broadcastToRoom(ws.roomId, {
                type: 'turnEnded',
                room: updatedRoom,
                nextPlayerId: room.players[nextPlayerIndex].id
              });

            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to end turn'
              }));
            }
            break;

          case 'buyProperty':
            try {
              const { propertyIndex, price } = message;
              if (!ws.roomId || !ws.playerId) break;

              const room = await storage.getRoom(ws.roomId);
              if (!room) break;

              const player = room.players.find(p => p.id === ws.playerId);
              if (!player || player.money < price) {
                ws.send(JSON.stringify({
                  type: 'error',
                  message: 'Insufficient funds'
                }));
                break;
              }

              // Update player money and properties
              await storage.updatePlayer(ws.roomId, ws.playerId, {
                money: player.money - price,
                properties: [...player.properties, propertyIndex]
              });

              const updatedRoom = await storage.getRoom(ws.roomId);

              broadcastToRoom(ws.roomId, {
                type: 'propertyBought',
                room: updatedRoom,
                playerId: ws.playerId,
                propertyIndex,
                price
              });

            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to buy property'
              }));
            }
            break;

          case 'sendMessage':
            try {
              const { message: messageText } = message;
              if (!ws.roomId || !ws.playerId) break;

              const room = await storage.getRoom(ws.roomId);
              if (!room) break;

              const player = room.players.find(p => p.id === ws.playerId);
              if (!player) break;

              const chatMessage = {
                playerId: ws.playerId,
                playerName: player.name,
                message: messageText,
                timestamp: new Date()
              };

              await storage.addChatMessage(ws.roomId, chatMessage);

              broadcastToRoom(ws.roomId, {
                type: 'newMessage',
                message: chatMessage
              });

            } catch (error) {
              ws.send(JSON.stringify({
                type: 'error',
                message: 'Failed to send message'
              }));
            }
            break;
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
        ws.send(JSON.stringify({
          type: 'error',
          message: 'Invalid message format'
        }));
      }
    });

    ws.on('close', async () => {
      connections.delete(connectionId);

      if (ws.roomId) {
        const roomClients = roomConnections.get(ws.roomId);
        if (roomClients) {
          roomClients.delete(connectionId);

          if (ws.playerId) {
            // Mark player as disconnected
            await storage.updatePlayer(ws.roomId, ws.playerId, { isConnected: false });

            const room = await storage.getRoom(ws.roomId);
            if (room) {
              // If host disconnected, set room to expire in 2 hours
              if (room.hostId === ws.playerId) {
                await storage.setRoomExpiration(ws.roomId, 2);
                console.log(`Host disconnected from room ${room.code}, room will expire in 2 hours`);
              }

              broadcastToRoom(ws.roomId, {
                type: 'playerDisconnected',
                room,
                playerId: ws.playerId
              });
            }
          }
        }
      }
    });
  });

  // REST API routes for room management
  app.post('/api/rooms', async (req, res) => {
    try {
      const { hostName } = req.body;
      const room = await storage.createRoom({ hostId: hostName, maxPlayers: 8 });
      res.json(room);
    } catch (error) {
      res.status(400).json({ message: 'Failed to create room' });
    }
  });

  app.get('/api/rooms/:code', async (req, res) => {
    try {
      const room = await storage.getRoomByCode(req.params.code);
      if (!room) {
        return res.status(404).json({ message: 'Room not found' });
      }
      res.json(room);
    } catch (error) {
      res.status(500).json({ message: 'Server error' });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { type Room, type Player, type InsertRoom, type InsertPlayer, type ChatMessage } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Room management
  createRoom(room: InsertRoom): Promise<Room>;
  getRoom(roomId: string): Promise<Room | undefined>;
  getRoomByCode(code: string): Promise<Room | undefined>;
  updateRoom(roomId: string, updates: Partial<Room>): Promise<Room | undefined>;
  deleteRoom(roomId: string): Promise<boolean>;

  // Player management
  addPlayerToRoom(roomId: string, player: InsertPlayer): Promise<Player | undefined>;
  updatePlayer(roomId: string, playerId: string, updates: Partial<Player>): Promise<Player | undefined>;
  removePlayerFromRoom(roomId: string, playerId: string): Promise<boolean>;

  // Chat management
  addChatMessage(roomId: string, message: ChatMessage): Promise<ChatMessage>;
  getChatMessages(roomId: string): Promise<ChatMessage[]>;

  // Game state management
  startGame(roomId: string): Promise<boolean>;
  updateGameState(roomId: string, gameState: any): Promise<boolean>;

  // Room persistence management
  setRoomExpiration(roomId: string, hoursFromNow: number): Promise<boolean>;
  cleanupExpiredRooms(): Promise<number>;
  isRoomExpired(roomId: string): Promise<boolean>;
}

export class MemStorage implements IStorage {
  private rooms: Map<string, Room>;
  private roomCodes: Map<string, string>; // code -> roomId mapping
  private chatMessages: Map<string, ChatMessage[]>;

  constructor() {
    this.rooms = new Map();
    this.roomCodes = new Map();
    this.chatMessages = new Map();
  }

  async createRoom(insertRoom: InsertRoom): Promise<Room> {
    const roomId = randomUUID();
    const code = this.generateRoomCode();

    const room: Room = {
      ...insertRoom,
      id: roomId,
      code,
      players: [],
      gameState: "waiting",
      currentPlayerIndex: 0,
      turnNumber: 1,
      createdAt: new Date(),
      isExpired: false,
    };

    this.rooms.set(roomId, room);
    this.roomCodes.set(code, roomId);
    this.chatMessages.set(roomId, []);
    return room;
  }

  async getRoom(roomId: string): Promise<Room | undefined> {
    return this.rooms.get(roomId);
  }

  async getRoomByCode(code: string): Promise<Room | undefined> {
    const roomId = this.roomCodes.get(code.toUpperCase());
    if (!roomId) return undefined;
    return this.rooms.get(roomId);
  }

  async updateRoom(roomId: string, updates: Partial<Room>): Promise<Room | undefined> {
    const room = this.rooms.get(roomId);
    if (!room) return undefined;

    const updatedRoom = { ...room, ...updates };
    this.rooms.set(roomId, updatedRoom);
    return updatedRoom;
  }

  async deleteRoom(roomId: string): Promise<boolean> {
    const room = this.rooms.get(roomId);
    if (!room) return false;

    this.rooms.delete(roomId);
    this.roomCodes.delete(room.code);
    this.chatMessages.delete(roomId);
    return true;
  }

  async addPlayerToRoom(roomId: string, insertPlayer: InsertPlayer): Promise<Player | undefined> {
    const room = this.rooms.get(roomId);
    if (!room || room.players.length >= room.maxPlayers) return undefined;

    const playerId = `${insertPlayer.name}-${Date.now()}`;
    const player: Player = {
      ...insertPlayer,
      id: playerId,
      money: 15000,
      position: 0,
      isInJail: false,
      jailTurns: 0,
      properties: [],
      isReady: false,
      isConnected: true,
    };

    room.players.push(player);
    this.rooms.set(roomId, room);
    return player;
  }

  async updatePlayer(roomId: string, playerId: string, updates: Partial<Player>): Promise<Player | undefined> {
    const room = this.rooms.get(roomId);
    if (!room) return undefined;

    const playerIndex = room.players.findIndex(p => p.id === playerId);
    if (playerIndex === -1) return undefined;

    room.players[playerIndex] = { ...room.players[playerIndex], ...updates };
    this.rooms.set(roomId, room);
    return room.players[playerIndex];
  }

  async removePlayer(roomId: string, playerId: string): Promise<boolean> {
    const room = this.rooms.get(roomId);
    if (!room) return false;

    const playerIndex = room.players.findIndex(p => p.id === playerId);
    if (playerIndex === -1) return false;

    // Remove the player
    room.players.splice(playerIndex, 1);

    this.rooms.set(roomId, room);
    return true;
  }

  async removePlayerFromRoom(roomId: string, playerId: string): Promise<boolean> {
    const room = this.rooms.get(roomId);
    if (!room) return false;

    const playerIndex = room.players.findIndex(p => p.id === playerId);
    if (playerIndex === -1) return false;

    room.players.splice(playerIndex, 1);

    // If host left and there are other players, make the first player the new host
    if (room.hostId === playerId && room.players.length > 0) {
      room.hostId = room.players[0].id;
    }

    this.rooms.set(roomId, room);
    return true;
  }

  async addChatMessage(roomId: string, message: ChatMessage): Promise<ChatMessage> {
    const messages = this.chatMessages.get(roomId) || [];
    messages.push(message);
    this.chatMessages.set(roomId, messages);
    return message;
  }

  async getChatMessages(roomId: string): Promise<ChatMessage[]> {
    return this.chatMessages.get(roomId) || [];
  }

  async startGame(roomId: string): Promise<boolean> {
    const room = this.rooms.get(roomId);
    if (!room || room.gameState !== "waiting") return false;

    room.gameState = "playing";
    room.currentPlayerIndex = 0;
    room.turnNumber = 1;

    // Reset all players to start position
    room.players.forEach(player => {
      player.position = 0;
      player.money = 15000;
      player.properties = [];
      player.isInJail = false;
      player.jailTurns = 0;
    });

    this.rooms.set(roomId, room);
    return true;
  }

  async updateGameState(roomId: string, gameState: any): Promise<boolean> {
    const room = this.rooms.get(roomId);
    if (!room) return false;

    Object.assign(room, gameState);
    this.rooms.set(roomId, room);
    return true;
  }

  async setRoomExpiration(roomId: string, hoursFromNow: number): Promise<boolean> {
    const room = this.rooms.get(roomId);
    if (!room) return false;

    const expiresAt = new Date();
    expiresAt.setHours(expiresAt.getHours() + hoursFromNow);

    room.expiresAt = expiresAt;
    this.rooms.set(roomId, room);
    return true;
  }

  async cleanupExpiredRooms(): Promise<number> {
    const now = new Date();
    let cleanedCount = 0;
    const roomsToDelete: string[] = [];

    this.rooms.forEach((room, roomId) => {
      if (room.expiresAt && now > room.expiresAt) {
        roomsToDelete.push(roomId);
      }
    });

    roomsToDelete.forEach(roomId => {
      const room = this.rooms.get(roomId);
      if (room) {
        this.rooms.delete(roomId);
        this.roomCodes.delete(room.code);
        this.chatMessages.delete(roomId);
        cleanedCount++;
      }
    });

    return cleanedCount;
  }

  async isRoomExpired(roomId: string): Promise<boolean> {
    const room = this.rooms.get(roomId);
    if (!room) return true;

    if (room.expiresAt && new Date() > room.expiresAt) {
      room.isExpired = true;
      this.rooms.set(roomId, room);
      return true;
    }

    return false;
  }

  private generateRoomCode(): string {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let result = '';
    for (let i = 0; i < 6; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    // Ensure uniqueness
    if (this.roomCodes.has(result)) {
      return this.generateRoomCode();
    }
    return result;
  }
}

export const storage = new MemStorage();

