import { useState, useEffect, useRef } from "react";
import { ChatMessage, Player } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";

interface ChatProps {
  messages: ChatMessage[];
  currentPlayer: Player | null;
  onSendMessage: (message: string) => void;
  isMobile?: boolean;
}

export default function Chat({ messages, currentPlayer, onSendMessage, isMobile = false }: ChatProps) {
  const [newMessage, setNewMessage] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (!newMessage.trim()) return;

    onSendMessage(newMessage.trim());
    setNewMessage("");
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (timestamp: Date) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const containerClass = isMobile 
    ? "flex flex-col h-80"
    : "bg-white rounded-xl shadow-2xl p-4 flex flex-col h-full";

  return (
    <div className={containerClass}>
      <div className="flex justify-between items-center mb-4">
        <h3 className="font-bold text-lg text-navy flex items-center">
          <i className="fas fa-comments mr-2"></i>
          Game Chat
        </h3>
        <div className="text-sm text-gray-500" data-testid="text-messages-count">
          {messages.length} messages
        </div>
      </div>

      {/* Chat Messages */}
      <ScrollArea className="flex-1 mb-4 bg-gray-50 rounded-lg p-3">
        <div className="space-y-3">
          {messages.length === 0 ? (
            <div className="text-center text-gray-500 py-8" data-testid="text-no-messages">
              <i className="fas fa-comments text-3xl mb-2"></i>
              <p>No messages yet</p>
              <p className="text-sm">Start the conversation!</p>
            </div>
          ) : (
            messages.map((message, index) => {
              const isOwnMessage = message.playerId === currentPlayer?.id;

              return (
                <div 
                  key={index}
                  className={`flex items-start space-x-2 ${isOwnMessage ? 'flex-row-reverse space-x-reverse' : ''}`}
                  data-testid={`message-${index}`}
                >
                  <div className="w-8 h-8 rounded-full flex items-center justify-center text-white text-xs font-bold bg-gray-400">
                    {message.playerName.charAt(0).toUpperCase()}
                  </div>
                  <div className={`flex-1 max-w-xs ${isOwnMessage ? 'text-right' : ''}`}>
                    <div className="flex items-baseline space-x-2 mb-1">
                      <span className="text-xs font-semibold text-gray-700" data-testid={`message-author-${index}`}>
                        {isOwnMessage ? 'You' : message.playerName}
                      </span>
                      <span className="text-xs text-gray-500" data-testid={`message-time-${index}`}>
                        {formatTime(message.timestamp)}
                      </span>
                    </div>
                    <div 
                      className={`chat-bubble ${
                        isOwnMessage ? 'chat-bubble-own' : 'chat-bubble-other'
                      }`}
                    >
                      <p className="text-sm" data-testid={`message-content-${index}`}>
                        {message.message}
                      </p>
                    </div>
                  </div>
                </div>
              );
            })
          )}
          <div ref={messagesEndRef} />
        </div>
      </ScrollArea>

      {/* Chat Input */}
      <div className="flex space-x-2">
        <Input
          data-testid="input-chat-message"
          placeholder="Type your message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          maxLength={500}
          className="flex-1"
        />
        <Button
          data-testid="button-send-message"
          onClick={handleSendMessage}
          disabled={!newMessage.trim()}
          className="bg-saffron hover:bg-orange-600 text-white"
        >
          <i className="fas fa-paper-plane"></i>
        </Button>
      </div>

      {/* Character Counter */}
      <div className="text-xs text-gray-500 mt-1 text-right">
        {newMessage.length}/500
      </div>
    </div>
  );
}

client/src/components/GameBoard.tsx.content:
import { Room, Player } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { GAME_PROPERTIES } from "@/lib/gameConstants";

interface GameBoardProps {
  room: Room;
  currentPlayer: Player | null;
  diceResult: {dice1: number, dice2: number} | null;
  isRolling: boolean;
  isMyTurn: boolean;
  onRollDice: () => void;
  onEndTurn: () => void;
  onPropertyClick: (propertyIndex: number) => void;
}

export default function GameBoard({
  room,
  currentPlayer,
  diceResult,
  isRolling,
  isMyTurn,
  onRollDice,
  onEndTurn,
  onPropertyClick
}: GameBoardProps) {
  const getDiceIcon = (value: number) => {
    const icons = ['', 'fa-dice-one', 'fa-dice-two', 'fa-dice-three', 'fa-dice-four', 'fa-dice-five', 'fa-dice-six'];
    return icons[value] || 'fa-dice';
  };

  const getPropertyOwner = (propertyIndex: number) => {
    return room.players.find(player => player.properties.includes(propertyIndex));
  };

  const renderPropertySpace = (property: typeof GAME_PROPERTIES[0], index: number, className: string = "") => {
    const owner = getPropertyOwner(index);
    const hasPlayers = room.players.some(player => player.position === index);
    const playersHere = room.players.filter(player => player.position === index);

    return (
      <div
        key={index}
        className={`board-space ${className} cursor-pointer hover:bg-gray-50 transition-colors relative`}
        onClick={() => onPropertyClick(index)}
        data-testid={`property-${index}`}
      >
        {/* Property Color Bar */}
        {property.colorGroup && property.colorGroup !== 'special' && (
          <div className={`property-color-bar ${getColorGroupClass(property.colorGroup)}`} />
        )}

        {/* Property Content */}
        <div className="flex-1 flex flex-col items-center justify-center text-center p-1">
          {property.type === 'special' ? (
            <>
              <i className={`fas ${getSpecialIcon(property.name)} text-lg mb-1`} />
              <span className="font-semibold text-xs">{property.name}</span>
              {property.name === 'START' && <span className="text-xs">₹2000</span>}
              {(property.name === 'Income Tax' || property.name === 'Super Tax') && (
                <span className="text-xs">₹{property.price || 0}</span>
              )}
            </>
          ) : (
            <>
              <span className="font-semibold text-xs">{property.name}</span>
              <span className="text-xs">₹{property.price?.toLocaleString()}</span>
            </>
          )}
        </div>

        {/* Owner Indicator */}
        {owner && (
          <div 
            className="absolute top-1 right-1 w-3 h-3 rounded-full border border-white"
            style={{ backgroundColor: owner.color }}
            title={`Owned by ${owner.name}`}
          />
        )}

        {/* Players on this space */}
        {hasPlayers && (
          <div className="absolute bottom-1 left-1 flex flex-wrap gap-0.5">
            {playersHere.map((player, playerIndex) => (
              <div
                key={player.id}
                className="player-token"
                style={{ 
                  backgroundColor: player.color,
                  zIndex: 10 + playerIndex
                }}
                title={player.name}
              />
            ))}
          </div>
        )}
      </div>
    );
  };

  const getColorGroupClass = (colorGroup: string) => {
    const colorMap: Record<string, string> = {
      'brown': 'bg-amber-700',
      'lightblue': 'bg-sky-300',
      'pink': 'bg-pink-400',
      'orange': 'bg-orange-500',
      'red': 'bg-red-500',
      'yellow': 'bg-yellow-400',
      'green': 'bg-green-500',
      'darkblue': 'bg-blue-700',
      'railroad': 'bg-gray-700',
      'utility': 'bg-yellow-300',
    };
    return colorMap[colorGroup] || 'bg-gray-400';
  };

  const getSpecialIcon = (name: string) => {
    const iconMap: Record<string, string> = {
      'START': 'fa-flag-checkered',
      'JAIL': 'fa-lock',
      'FREE PARKING': 'fa-car',
      'GO TO JAIL': 'fa-hand-point-right',
      'Chance': 'fa-dice',
      'Community Chest': 'fa-question-circle',
      'Income Tax': 'fa-coins',
      'Super Tax': 'fa-coins',
      'Electric Company': 'fa-lightbulb',
      'Water Works': 'fa-tint'
    };
    return iconMap[name] || 'fa-building';
  };

  // Arrange properties in board layout (11x11 grid)
  const arrangeProperties = () => {
    const arranged: (typeof GAME_PROPERTIES[0] | null)[][] = Array(11).fill(null).map(() => Array(11).fill(null));

    // Bottom row (START to JAIL)
    for (let i = 0; i <= 10; i++) {
      arranged[10][i] = GAME_PROPERTIES[i];
    }

    // Right column (excluding corners)
    for (let i = 1; i <= 9; i++) {
      arranged[10-i][10] = GAME_PROPERTIES[10 + i];
    }

    // Top row (FREE PARKING to GO TO JAIL)
    for (let i = 1; i <= 9; i++) {
      arranged[0][10-i] = GAME_PROPERTIES[20 + i];
    }

    // Left column (excluding corners)
    for (let i = 1; i <= 9; i++) {
      arranged[i][0] = GAME_PROPERTIES[30 + i];
    }

    return arranged;
  };

  const boardLayout = arrangeProperties();

  return (
    <div className="bg-forest-green p-4 rounded-xl shadow-2xl">
      <div className="aspect-square bg-cream rounded-lg p-2 relative max-w-4xl mx-auto">
        {/* Board Grid */}
        <div className="grid grid-cols-11 grid-rows-11 h-full gap-0.5">
          {boardLayout.map((row, rowIndex) =>
            row.map((property, colIndex) => {
              if (!property) {
                // Center area
                if (rowIndex >= 2 && rowIndex <= 8 && colIndex >= 2 && colIndex <= 8) {
                  if (rowIndex === 5 && colIndex === 5) {
                    // Center board area - only render once
                    return (
                      <div
                        key={`center-${rowIndex}-${colIndex}`}
                        className="col-span-7 row-span-7 bg-navy rounded-lg flex flex-col items-center justify-center text-white p-4 relative"
                      >
                        {/* Game Logo */}
                        <div className="text-center mb-6">
                          <h1 className="text-2xl md:text-4xl font-bold text-saffron mb-2">VYAPAAR</h1>
                          <p className="text-sm md:text-lg opacity-90">Indian Monopoly Game</p>
                        </div>

                        {/* Dice Section */}
                        <div className="flex items-center space-x-4 mb-6">
                          <div 
                            className={`w-12 h-12 bg-white rounded-lg flex items-center justify-center text-navy text-xl font-bold shadow-lg ${isRolling ? 'animate-dice-roll' : ''}`}
                            data-testid="dice-1"
                          >
                            <i className={`fas ${diceResult ? getDiceIcon(diceResult.dice1) : 'fa-dice'}`}></i>
                          </div>
                          <div 
                            className={`w-12 h-12 bg-white rounded-lg flex items-center justify-center text-navy text-xl font-bold shadow-lg ${isRolling ? 'animate-dice-roll' : ''}`}
                            data-testid="dice-2"
                          >
                            <i className={`fas ${diceResult ? getDiceIcon(diceResult.dice2) : 'fa-dice'}`}></i>
                          </div>
                        </div>

                        {/* Current Player Turn */}
                        {currentPlayer && (
                          <div className="text-center mb-4">
                            <p className="text-sm opacity-75 mb-2">
                              {isMyTurn ? "Your Turn" : "Current Turn"}
                            </p>
                            <div className="flex items-center justify-center space-x-2">
                              <div 
                                className="w-8 h-8 rounded-full flex items-center justify-center text-white font-bold text-sm"
                                style={{ backgroundColor: room.players[room.currentPlayerIndex].color }}
                              >
                                {room.players[room.currentPlayerIndex].avatar}
                              </div>
                              <span className="font-semibold">
                                {isMyTurn ? "You" : room.players[room.currentPlayerIndex].name}
                              </span>
                            </div>
                          </div>
                        )}

                        {/* Action Buttons */}
                        {isMyTurn && (
                          <div className="space-y-2">
                            <Button
                              data-testid="button-roll-dice"
                              onClick={onRollDice}
                              disabled={isRolling}
                              className="bg-saffron hover:bg-orange-600 px-6 py-2 rounded-lg font-semibold transition-colors"
                            >
                              {isRolling ? (
                                <>
                                  <i className="fas fa-spinner animate-spin mr-2"></i>
                                  Rolling...
                                </>
                              ) : (
                                <>
                                  <i className="fas fa-dice mr-2"></i>
                                  Roll Dice
                                </>
                              )}
                            </Button>

                            {diceResult && (
                              <Button
                                data-testid="button-end-turn"
                                onClick={onEndTurn}
                                variant="outline"
                                className="w-full"
                              >
                                <i className="fas fa-forward mr-2"></i>
                                End Turn
                              </Button>
                            )}
                          </div>
                        )}

                        {/* Dice Result Display */}
                        {diceResult && (
                          <div className="mt-4 text-center">
                            <p className="text-sm opacity-75">Last Roll</p>
                            <p className="text-lg font-bold" data-testid="text-dice-total">
                              {diceResult.dice1 + diceResult.dice2}
                            </p>
                          </div>
                        )}
                      </div>
                    );
                  }
                  return null; // Other center grid cells are covered by the span
                }
                return <div key={`empty-${rowIndex}-${colIndex}`} />;
              }

              // Calculate property index
              const propertyIndex = GAME_PROPERTIES.findIndex(p => p.id === property.id);

              // Determine if it's a corner
              const isCorner = (rowIndex === 0 || rowIndex === 10) && (colIndex === 0 || colIndex === 10);
              const cornerClass = isCorner ? 'board-corner' : '';

              // Add rotation for side properties
              let rotationClass = '';
              if (rowIndex === 0 && colIndex !== 0 && colIndex !== 10) {
                // Top row - no rotation needed
              } else if (rowIndex === 10 && colIndex !== 0 && colIndex !== 10) {
                // Bottom row - no rotation needed
              } else if (colIndex === 0 && rowIndex !== 0 && rowIndex !== 10) {
                // Left side - rotate 90 degrees
                rotationClass = 'transform rotate-90';
              } else if (colIndex === 10 && rowIndex !== 0 && rowIndex !== 10) {
                // Right side - rotate -90 degrees
                rotationClass = 'transform -rotate-90';
              }

              return (
                <div
                  key={`${rowIndex}-${colIndex}`}
                  className={`${cornerClass} ${rotationClass}`}
                >
                  {renderPropertySpace(property, propertyIndex)}
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/PlayerInfo.tsx.content:
import { Room, Player } from "@shared/schema";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { GAME_PROPERTIES } from "@/lib/gameConstants";

interface PlayerInfoProps {
  room: Room;
  currentPlayer: Player | null;
  isMyTurn: boolean;
  onRollDice: () => void;
  onEndTurn: () => void;
  isRolling: boolean;
}

export default function PlayerInfo({
  room,
  currentPlayer,
  isMyTurn,
  onRollDice,
  onEndTurn,
  isRolling
}: PlayerInfoProps) {
  if (!currentPlayer) return null;

  const getPropertyName = (propertyIndex: number) => {
    return GAME_PROPERTIES[propertyIndex]?.name || `Property ${propertyIndex}`;
  };

  const getPropertyValue = (propertyIndex: number) => {
    return GAME_PROPERTIES[propertyIndex]?.price || 0;
  };

  const totalPropertyValue = currentPlayer.properties.reduce((total, propIndex) => {
    return total + getPropertyValue(propIndex);
  }, 0);

  const gameStats = {
    turnNumber: room.turnNumber,
    gameTime: Math.floor((Date.now() - new Date(room.createdAt).getTime()) / 1000 / 60), // minutes
  };

  return (
    <div className="space-y-4">
      {/* Current Player Info */}
      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="text-lg text-navy flex items-center">
            <i className="fas fa-user mr-2"></i>
            Your Info
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center space-x-3">
            <div 
              className="w-12 h-12 rounded-full flex items-center justify-center text-white font-bold text-lg shadow-lg"
              style={{ backgroundColor: currentPlayer.color }}
              data-testid="player-avatar"
            >
              {currentPlayer.avatar}
            </div>
            <div>
              <p className="font-semibold" data-testid="text-player-name">{currentPlayer.name}</p>
              <p className="text-sm text-gray-500">
                {isMyTurn ? "Your Turn" : "Waiting"}
              </p>
            </div>
          </div>

          <div className="bg-indian-green text-white p-3 rounded-lg text-center">
            <p className="text-sm opacity-90">Cash Balance</p>
            <p className="text-2xl font-bold" data-testid="text-player-balance">
              ₹{currentPlayer.money.toLocaleString()}
            </p>
          </div>

          {/* Quick Actions */}
          <div className="space-y-2">
            <Button
              data-testid="button-roll-dice-sidebar"
              onClick={onRollDice}
              disabled={!isMyTurn || isRolling}
              className="w-full bg-saffron hover:bg-orange-600 text-white"
            >
              <i className={`fas ${isRolling ? 'fa-spinner animate-spin' : 'fa-dice'} mr-2`}></i>
              {isRolling ? 'Rolling...' : 'Roll Dice'}
            </Button>

            <Button
              data-testid="button-end-turn-sidebar"
              onClick={onEndTurn}
              disabled={!isMyTurn}
              variant="outline"
              className="w-full"
            >
              <i className="fas fa-forward mr-2"></i>
              End Turn
            </Button>
          </div>

          {/* Properties Owned */}
          {currentPlayer.properties.length > 0 && (
            <div className="border-t pt-4">
              <h4 className="font-semibold text-dark-slate mb-3 flex items-center">
                <i className="fas fa-building mr-2"></i>
                Properties Owned ({currentPlayer.properties.length})
              </h4>
              <div className="space-y-2 max-h-32 overflow-y-auto">
                {currentPlayer.properties.map((propIndex) => (
                  <div key={propIndex} className="flex justify-between items-center p-2 bg-gray-50 rounded text-sm">
                    <span className="font-medium" data-testid={`property-owned-${propIndex}`}>
                      {getPropertyName(propIndex)}
                    </span>
                    <span className="text-green-600 font-semibold">
                      ₹{getPropertyValue(propIndex).toLocaleString()}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Player Statistics */}
      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="text-lg text-navy flex items-center">
            <i className="fas fa-chart-bar mr-2"></i>
            Statistics
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          <div className="flex justify-between">
            <span className="text-gray-600">Properties Owned</span>
            <span className="font-semibold" data-testid="text-properties-count">
              {currentPlayer.properties.length}
            </span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-600">Total Property Value</span>
            <span className="font-semibold" data-testid="text-total-property-value">
              ₹{totalPropertyValue.toLocaleString()}
            </span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-600">Net Worth</span>
            <span className="font-semibold text-green-600" data-testid="text-net-worth">
              ₹{(currentPlayer.money + totalPropertyValue).toLocaleString()}
            </span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-600">Position</span>
            <span className="font-semibold" data-testid="text-player-position">
              {getPropertyName(currentPlayer.position)}
            </span>
          </div>
        </CardContent>
      </Card>

      {/* All Players */}
      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="text-lg text-navy flex items-center">
            <i className="fas fa-users mr-2"></i>
            All Players
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            {room.players.map((player) => {
              const isCurrentTurn = room.players[room.currentPlayerIndex].id === player.id;
              const playerPropertyValue = player.properties.reduce((total, propIndex) => {
                return total + getPropertyValue(propIndex);
              }, 0);

              return (
                <div 
                  key={player.id}
                  className={`flex items-center justify-between p-2 rounded-lg border ${
                    isCurrentTurn ? 'border-saffron bg-orange-50' : 'bg-gray-50'
                  }`}
                >
                  <div className="flex items-center space-x-2">
                    <div 
                      className="w-6 h-6 rounded-full flex items-center justify-center text-white text-xs font-bold"
                      style={{ backgroundColor: player.color }}
                    >
                      {player.avatar}
                    </div>
                    <div>
                      <span className="font-semibold text-sm" data-testid={`text-player-list-name-${player.name}`}>
                        {player.name}
                        {player.id === currentPlayer.id && " (You)"}
                      </span>
                      {isCurrentTurn && (
                        <Badge variant="outline" className="ml-2 text-xs bg-saffron text-white">
                          Turn
                        </Badge>
                      )}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="font-semibold text-sm" data-testid={`text-player-money-${player.name}`}>
                      ₹{player.money.toLocaleString()}
                    </div>
                    <div className="text-xs text-gray-500">
                      Net: ₹{(player.money + playerPropertyValue).toLocaleString()}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>

      {/* Game Stats */}
      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="text-lg text-navy flex items-center">
            <i className="fas fa-clock mr-2"></i>
            Game Stats
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 gap-4 text-center">
            <div className="bg-gray-50 p-3 rounded-lg">
              <div className="text-xl font-bold text-saffron" data-testid="text-turn-number">
                {gameStats.turnNumber}
              </div>
              <div className="text-xs text-gray-600">Turn Number</div>
            </div>
            <div className="bg-gray-50 p-3 rounded-lg">
              <div className="text-xl font-bold text-indian-green" data-testid="text-game-time">
                {gameStats.gameTime}m
              </div>
              <div className="text-xs text-gray-600">Game Time</div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Quick Actions */}
      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="text-lg text-navy flex items-center">
            <i className="fas fa-university mr-2"></i>
            Quick Actions
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-2">
          <Button 
            variant="outline" 
            className="w-full text-sm"
            disabled
            data-testid="button-mortgage-property"
          >
            <i className="fas fa-home mr-2"></i>
            Mortgage Property
          </Button>
          <Button 
            variant="outline" 
            className="w-full text-sm"
            disabled
            data-testid="button-trade-player"
          >
            <i className="fas fa-exchange-alt mr-2"></i>
            Trade with Player
          </Button>
          <Button 
            variant="outline" 
            className="w-full text-sm"
            disabled
            data-testid="button-build-houses"
          >
            <i className="fas fa-hammer mr-2"></i>
            Build Houses
          </Button>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/PropertyModal.tsx.content:
import { Room, Player } from "@shared/schema";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { GAME_PROPERTIES } from "@/lib/gameConstants";

interface PropertyModalProps {
  show: boolean;
  propertyIndex: number | null;
  room: Room | null;
  currentPlayer: Player | null;
  onBuy: (propertyIndex: number, price: number) => void;
  onClose: () => void;
}

export default function PropertyModal({
  show,
  propertyIndex,
  room,
  currentPlayer,
  onBuy,
  onClose
}: PropertyModalProps) {
  if (!show || propertyIndex === null || !room || !currentPlayer) {
    return null;
  }

  const property = GAME_PROPERTIES[propertyIndex];
  if (!property) {
    return null;
  }

  const owner = room.players.find(player => player.properties.includes(propertyIndex));
  const canBuy = !owner && 
                 property.type !== 'special' && 
                 property.price && 
                 currentPlayer.money >= property.price;

  const getColorGroupClass = (colorGroup: string) => {
    const colorMap: Record<string, string> = {
      'brown': 'bg-amber-700',
      'lightblue': 'bg-sky-300',
      'pink': 'bg-pink-400',
      'orange': 'bg-orange-500',
      'red': 'bg-red-500',
      'yellow': 'bg-yellow-400',
      'green': 'bg-green-500',
      'darkblue': 'bg-blue-700',
      'railroad': 'bg-gray-700',
      'utility': 'bg-yellow-300',
    };
    return colorMap[colorGroup] || 'bg-gray-400';
  };

  const handleBuy = () => {
    if (property.price) {
      onBuy(propertyIndex, property.price);
    }
  };

  return (
    <Dialog open={show} onOpenChange={onClose}>
      <DialogContent className="max-w-md" data-testid="property-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-3">
            {property.colorGroup && property.colorGroup !== 'special' && (
              <div className={`w-6 h-6 rounded ${getColorGroupClass(property.colorGroup)}`} />
            )}
            <div>
              <h3 className="text-xl font-bold text-navy" data-testid="property-name">
                {property.name}
              </h3>
              {property.colorGroup && property.colorGroup !== 'special' && (
                <p className="text-sm text-gray-600 capitalize">
                  {property.colorGroup} Property Group
                </p>
              )}
            </div>
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Property Status */}
          <div className="text-center">
            {owner ? (
              <div>
                <Badge variant="outline" className="mb-2">Owned</Badge>
                <p className="text-sm text-gray-600">
                  Owned by <span className="font-semibold">{owner.name}</span>
                </p>
              </div>
            ) : property.type === 'special' ? (
              <Badge variant="outline">Special Space</Badge>
            ) : (
              <Badge className="bg-green-100 text-green-800">Available for Purchase</Badge>
            )}
          </div>

          {/* Property Details */}
          {property.type !== 'special' && property.price && (
            <div className="space-y-3">
              <div className="text-center">
                <div className="text-3xl font-bold text-navy mb-2" data-testid="property-price">
                  ₹{property.price.toLocaleString()}
                </div>
                <p className="text-sm text-gray-600">Purchase Price</p>
              </div>

              {property.rent && (
                <div className="space-y-2">
                  <h4 className="font-semibold text-navy">Rent Information</h4>
                  <div className="bg-gray-50 rounded-lg p-3 space-y-1 text-sm">
                    <div className="flex justify-between">
                      <span>Base Rent:</span>
                      <span className="font-semibold" data-testid="property-rent-base">
                        ₹{property.rent[0]?.toLocaleString() || 0}
                      </span>
                    </div>
                    {property.type === 'property' && (
                      <>
                        <div className="flex justify-between">
                          <span>With Color Group:</span>
                          <span className="font-semibold">
                            ₹{(property.rent[0] * 2)?.toLocaleString() || 0}
                          </span>
                        </div>
                        <div className="flex justify-between">
                          <span>With 1 House:</span>
                          <span className="font-semibold">
                            ₹{property.rent[1]?.toLocaleString() || 0}
                          </span>
                        </div>
                        <div className="flex justify-between">
                          <span>With 2 Houses:</span>
                          <span className="font-semibold">
                            ₹{property.rent[2]?.toLocaleString() || 0}
                          </span>
                        </div>
                        <div className="flex justify-between">
                          <span>With 3 Houses:</span>
                          <span className="font-semibold">
                            ₹{property.rent[3]?.toLocaleString() || 0}
                          </span>
                        </div>
                        <div className="flex justify-between">
                          <span>With 4 Houses:</span>
                          <span className="font-semibold">
                            ₹{property.rent[4]?.toLocaleString() || 0}
                          </span>
                        </div>
                        <div className="flex justify-between">
                          <span>With Hotel:</span>
                          <span className="font-semibold">
                            ₹{property.rent[5]?.toLocaleString() || 0}
                          </span>
                        </div>
                      </>
                    )}
                  </div>

                  {property.type === 'property' && property.houseCost && (
                    <div className="bg-blue-50 rounded-lg p-3 text-sm">
                      <div className="flex justify-between">
                        <span>House Cost:</span>
                        <span className="font-semibold">
                          ₹{property.houseCost.toLocaleString()} each
                        </span>
                      </div>
                      <div className="flex justify-between">
                        <span>Hotel Cost:</span>
                        <span className="font-semibold">
                          ₹{property.houseCost.toLocaleString()} + 4 houses
                        </span>
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
          )}

          {/* Special Space Information */}
          {property.type === 'special' && (
            <div className="text-center text-gray-600">
              <p>This is a special space on the board.</p>
              {property.name === 'START' && <p>Collect ₹2,000 when passing!</p>}
              {property.name === 'JAIL' && <p>Just visiting or serving time.</p>}
              {property.name === 'FREE PARKING' && <p>Take a break here!</p>}
              {property.name === 'GO TO JAIL' && <p>Go directly to jail!</p>}
              {property.name.includes('Tax') && property.price && (
                <p>Pay ₹{property.price.toLocaleString()} to the bank.</p>
              )}
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex space-x-3">
            {canBuy ? (
              <>
                <Button
                  data-testid="button-buy-property"
                  onClick={handleBuy}
                  className="flex-1 bg-indian-green hover:bg-green-700 text-white"
                >
                  <i className="fas fa-shopping-cart mr-2"></i>
                  Buy Property
                </Button>
                <Button
                  data-testid="button-pass-property"
                  variant="outline"
                  onClick={onClose}
                  className="flex-1"
                >
                  Pass
                </Button>
              </>
            ) : (
              <Button
                data-testid="button-close-property"
                onClick={onClose}
                className="flex-1"
                variant="outline"
              >
                Close
              </Button>
            )}
          </div>

          {/* Purchase Warning */}
          {canBuy && property.price && (
            <div className="text-center text-sm text-gray-600">
              <p>
                After purchase, you'll have{' '}
                <span className="font-semibold">
                  ₹{(currentPlayer.money - property.price).toLocaleString()}
                </span>{' '}
                remaining.
              </p>
            </div>
          )}

          {/* Insufficient Funds Warning */}
          {!owner && property.type !== 'special' && property.price && currentPlayer.money < property.price && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-3 text-center">
              <p className="text-sm text-red-600">
                <i className="fas fa-exclamation-triangle mr-2"></i>
                Insufficient funds to purchase this property.
              </p>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

