import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { Room, Player, ChatMessage } from "@shared/schema";
import { useSocket } from "@/hooks/useSocket";
import { useToast } from "@/hooks/use-toast";
import GameBoard from "@/components/GameBoard";
import PlayerInfo from "@/components/PlayerInfo";
import Chat from "@/components/Chat";
import PropertyModal from "@/components/PropertyModal";
import RulesModal from "@/components/RulesModal";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

interface GameProps {
  roomCode: string;
}

export default function Game({ roomCode }: GameProps) {
  const [, setLocation] = useLocation();
  const [room, setRoom] = useState<Room | null>(null);
  const [currentPlayer, setCurrentPlayer] = useState<Player | null>(null);
  const [chatMessages, setChatMessages] = useState<ChatMessage[]>([]);
  const [showPropertyModal, setShowPropertyModal] = useState(false);
  const [showRulesModal, setShowRulesModal] = useState(false);
  const [selectedProperty, setSelectedProperty] = useState<number | null>(null);
  const [diceResult, setDiceResult] = useState<{dice1: number, dice2: number} | null>(null);
  const [isRolling, setIsRolling] = useState(false);
  const [showMobileChat, setShowMobileChat] = useState(false);

  const { socket, isConnected } = useSocket();
  const { toast } = useToast();

  useEffect(() => {
    if (!socket || !isConnected) return;

    const handleMessage = (event: MessageEvent) => {
      const data = JSON.parse(event.data);

      switch (data.type) {
        case 'roomJoined':
        case 'gameStarted':
        case 'diceRolled':
        case 'turnEnded':
        case 'propertyBought':
        case 'playerDisconnected':
          setRoom(data.room);
          if (data.type === 'diceRolled' && data.playerId === currentPlayer?.id) {
            setDiceResult({ dice1: data.dice1, dice2: data.dice2 });
            setIsRolling(false);
            if (data.passedStart) {
              toast({
                title: "Passed START!",
                description: "You collected ₹2,000",
                variant: "default",
              });
            }
          }
          break;

        case 'newMessage':
          setChatMessages(prev => [...prev, data.message]);
          break;

        case 'error':
          setIsRolling(false);
          toast({
            title: "Error",
            description: data.message,
            variant: "destructive",
          });
          // If room not found, redirect to home
          if (data.message === 'Room not found' || data.message === 'Player not found in room') {
            setTimeout(() => setLocation('/'), 2000);
          }
          break;
      }
    };

    socket.addEventListener('message', handleMessage);

    // Auto-rejoin the game room
    const playerId = localStorage.getItem('playerId');
    const playerName = localStorage.getItem('playerName');

    if (!playerId || !playerName) {
      toast({
        title: "No Player Data",
        description: "Please create or join a room first",
        variant: "destructive",
      });
      setLocation('/');
      return;
    }

    // Join the game room
    socket.send(JSON.stringify({
      type: 'rejoinRoom',
      roomCode: roomCode,
      playerId: playerId
    }));

    return () => {
      socket.removeEventListener('message', handleMessage);
    };
  }, [socket, isConnected, currentPlayer, toast]);

  useEffect(() => {
    const playerId = localStorage.getItem('playerId');
    if (room && playerId) {
      const player = room.players.find(p => p.id === playerId);
      setCurrentPlayer(player || null);
    }
  }, [room]);

  const handleRollDice = () => {
    if (!socket || !currentPlayer || isRolling) return;

    setIsRolling(true);
    setDiceResult(null);

    socket.send(JSON.stringify({
      type: 'rollDice'
    }));
  };

  const handleEndTurn = () => {
    if (!socket) return;

    socket.send(JSON.stringify({
      type: 'endTurn'
    }));
  };

  const handleBuyProperty = (propertyIndex: number, price: number) => {
    if (!socket) return;

    socket.send(JSON.stringify({
      type: 'buyProperty',
      propertyIndex,
      price
    }));

    setShowPropertyModal(false);
    setSelectedProperty(null);
  };

  const handleSendMessage = (message: string) => {
    if (!socket) return;

    socket.send(JSON.stringify({
      type: 'sendMessage',
      message
    }));
  };

  const handlePropertyClick = (propertyIndex: number) => {
    setSelectedProperty(propertyIndex);
    setShowPropertyModal(true);
  };

  if (!room) {
    return (
      <div className="min-h-screen bg-cream flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-saffron mb-4 mx-auto"></div>
          <p className="text-dark-slate">Loading game...</p>
        </div>
      </div>
    );
  }

  const currentTurnPlayer = room.players[room.currentPlayerIndex];
  const isMyTurn = currentPlayer?.id === currentTurnPlayer?.id;

  return (
    <div className="min-h-screen bg-cream">
      {/* Navigation Header */}
      <nav className="bg-navy text-white p-4 shadow-lg">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-2">
            <i className="fas fa-building text-saffron text-2xl"></i>
            <h1 className="text-2xl font-bold text-saffron">VYAPAAR</h1>
            <span className="text-sm opacity-75">Game</span>
          </div>
          <div className="flex items-center space-x-4">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setShowRulesModal(true)}
              data-testid="button-rules"
              className="text-white border-white hover:bg-white hover:text-navy"
            >
              <i className="fas fa-book mr-2"></i>
              Rules
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setShowMobileChat(true)}
              data-testid="button-mobile-chat"
              className="text-white border-white hover:bg-white hover:text-navy lg:hidden"
            >
              <i className="fas fa-comments mr-2"></i>
              Chat
            </Button>
            <span className="text-sm">
              Room: <span className="font-mono bg-saffron text-navy px-2 py-1 rounded" data-testid="text-game-room-code">{room.code}</span>
            </span>
          </div>
        </div>
      </nav>

      <div className="container mx-auto px-4 py-6">
        <div className="grid lg:grid-cols-4 gap-6 relative">
          {/* Game Board Section */}
          <div className="lg:col-span-3">
            <GameBoard
              room={room}
              currentPlayer={currentPlayer}
              diceResult={diceResult}
              isRolling={isRolling}
              isMyTurn={isMyTurn}
              onRollDice={handleRollDice}
              onEndTurn={handleEndTurn}
              onPropertyClick={handlePropertyClick}
            />

            {/* Mobile Player Info */}
            <div className="mt-6 lg:hidden">
              <PlayerInfo
                room={room}
                currentPlayer={currentPlayer}
                isMyTurn={isMyTurn}
                onRollDice={handleRollDice}
                onEndTurn={handleEndTurn}
                isRolling={isRolling}
              />
            </div>
          </div>

          {/* Right Sidebar (Desktop) */}
          <div className="lg:col-span-1 hidden lg:block">
            <PlayerInfo
              room={room}
              currentPlayer={currentPlayer}
              isMyTurn={isMyTurn}
              onRollDice={handleRollDice}
              onEndTurn={handleEndTurn}
              isRolling={isRolling}
            />
          </div>

          {/* Desktop Chat Sidebar */}
          <div className="hidden lg:block fixed right-4 top-20 bottom-4 w-80 z-40">
            <Chat
              messages={chatMessages}
              currentPlayer={currentPlayer}
              onSendMessage={handleSendMessage}
            />
          </div>
        </div>
      </div>

      {/* Mobile Chat Overlay */}
      {showMobileChat && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 lg:hidden">
          <div className="absolute bottom-0 left-0 right-0 bg-white rounded-t-xl p-4 h-96 animate-slide-up">
            <div className="flex justify-between items-center mb-4">
              <h3 className="font-bold text-lg">Game Chat</h3>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowMobileChat(false)}
                data-testid="button-close-mobile-chat"
              >
                <i className="fas fa-times text-xl"></i>
              </Button>
            </div>
            <div className="h-full">
              <Chat
                messages={chatMessages}
                currentPlayer={currentPlayer}
                onSendMessage={handleSendMessage}
                isMobile={true}
              />
            </div>
          </div>
        </div>
      )}

      {/* Current Turn Indicator */}
      <div className="fixed top-20 left-1/2 transform -translate-x-1/2 z-30">
        <div className="bg-white rounded-full px-6 py-3 shadow-lg border-2 border-saffron">
          <div className="flex items-center space-x-3">
            <div 
              className="w-8 h-8 rounded-full flex items-center justify-center text-white font-bold text-sm"
              style={{ backgroundColor: currentTurnPlayer.color }}
            >
              {currentTurnPlayer.avatar}
            </div>
            <div>
              <p className="font-semibold text-sm" data-testid="text-current-turn">
                {isMyTurn ? "Your Turn" : `${currentTurnPlayer.name}'s Turn`}
              </p>
              <p className="text-xs text-gray-500">Turn #{room.turnNumber}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Modals */}
      <PropertyModal
        show={showPropertyModal}
        propertyIndex={selectedProperty}
        room={room}
        currentPlayer={currentPlayer}
        onBuy={handleBuyProperty}
        onClose={() => {
          setShowPropertyModal(false);
          setSelectedProperty(null);
        }}
      />

      <RulesModal
        show={showRulesModal}
        onClose={() => setShowRulesModal(false)}
      />
    </div>
  );
}

client/src/pages/Home.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useSocket } from "@/hooks/useSocket";
import { useToast } from "@/hooks/use-toast";

export default function Home() {
  const [, setLocation] = useLocation();
  const [playerName, setPlayerName] = useState("");
  const [roomCode, setRoomCode] = useState("");
  const [maxPlayers, setMaxPlayers] = useState(4);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showJoinForm, setShowJoinForm] = useState(false);
  const { toast } = useToast();
  const { socket, isConnected } = useSocket();

  const playerColors = [
    { name: "Red", value: "#EF4444", bg: "bg-red-500" },
    { name: "Blue", value: "#3B82F6", bg: "bg-blue-500" },
    { name: "Green", value: "#22C55E", bg: "bg-green-500" },
    { name: "Purple", value: "#A855F7", bg: "bg-purple-500" },
    { name: "Orange", value: "#F97316", bg: "bg-orange-500" },
    { name: "Pink", value: "#EC4899", bg: "bg-pink-500" },
    { name: "Cyan", value: "#06B6D4", bg: "bg-cyan-500" },
    { name: "Yellow", value: "#EAB308", bg: "bg-yellow-500" },
  ];

  const [selectedColor, setSelectedColor] = useState(playerColors[0]);

  const handleCreateRoom = () => {
    if (!playerName.trim()) {
      toast({
        title: "Error",
        description: "Please enter your name",
        variant: "destructive",
      });
      return;
    }

    if (!isConnected || !socket) {
      toast({
        title: "Connection Error",
        description: "Not connected to server",
        variant: "destructive",
      });
      return;
    }

    socket.send(JSON.stringify({
      type: 'createRoom',
      hostName: playerName.trim(),
      maxPlayers: maxPlayers
    }));

    // Listen for room creation response
    const handleMessage = (event: MessageEvent) => {
      const data = JSON.parse(event.data);
      if (data.type === 'roomCreated') {
        localStorage.setItem('playerId', data.playerId);
        localStorage.setItem('playerName', playerName.trim());
        setLocation(`/lobby/${data.room.code}`);
        socket.removeEventListener('message', handleMessage);
      } else if (data.type === 'error') {
        toast({
          title: "Error",
          description: data.message,
          variant: "destructive",
        });
        socket.removeEventListener('message', handleMessage);
      }
    };

    socket.addEventListener('message', handleMessage);
  };

  const handleJoinRoom = () => {
    if (!playerName.trim()) {
      toast({
        title: "Error",
        description: "Please enter your name",
        variant: "destructive",
      });
      return;
    }

    if (!roomCode.trim()) {
      toast({
        title: "Error",
        description: "Please enter room code",
        variant: "destructive",
      });
      return;
    }

    if (!isConnected || !socket) {
      toast({
        title: "Connection Error",
        description: "Not connected to server",
        variant: "destructive",
      });
      return;
    }

    socket.send(JSON.stringify({
      type: 'joinRoom',
      roomCode: roomCode.trim().toUpperCase(),
      playerName: playerName.trim()
    }));

    // Listen for room join response
    const handleMessage = (event: MessageEvent) => {
      const data = JSON.parse(event.data);
      if (data.type === 'roomJoined') {
        localStorage.setItem('playerId', data.playerId);
        localStorage.setItem('playerName', playerName.trim());
        setLocation(`/lobby/${roomCode.trim().toUpperCase()}`);
        socket.removeEventListener('message', handleMessage);
      } else if (data.type === 'error') {
        toast({
          title: "Error",
          description: data.message,
          variant: "destructive",
        });
        socket.removeEventListener('message', handleMessage);
      }
    };

    socket.addEventListener('message', handleMessage);
  };

  return (
    <div className="min-h-screen bg-cream flex items-center justify-center p-4">
      <div className="w-full max-w-4xl">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="bg-gradient-to-r from-saffron via-white to-indian-green p-8 rounded-xl shadow-2xl mb-8">
            <h1 className="text-5xl md:text-6xl font-bold navy mb-4">VYAPAAR</h1>
            <p className="text-xl dark-slate mb-8">Experience the Classic Indian Monopoly Game</p>

            {!showCreateForm && !showJoinForm && (
              <div className="grid md:grid-cols-2 gap-6">
                <Button
                  data-testid="button-create-room"
                  onClick={() => setShowCreateForm(true)}
                  className="bg-saffron hover:bg-orange-600 text-white font-semibold py-6 px-8 rounded-xl text-lg shadow-lg transition-all transform hover:scale-105"
                >
                  <i className="fas fa-plus-circle mb-2 text-2xl block"></i>
                  Create New Room
                </Button>
                <Button
                  data-testid="button-join-room"
                  onClick={() => setShowJoinForm(true)}
                  className="bg-indian-green hover:bg-green-700 text-white font-semibold py-6 px-8 rounded-xl text-lg shadow-lg transition-all transform hover:scale-105"
                >
                  <i className="fas fa-sign-in-alt mb-2 text-2xl block"></i>
                  Join Existing Room
                </Button>
              </div>
            )}
          </div>
        </div>

        {/* Create Room Form */}
        {showCreateForm && (
          <Card className="mb-6">
            <CardHeader>
              <CardTitle className="text-2xl text-navy flex items-center">
                <i className="fas fa-plus-circle mr-2"></i>
                Create New Room
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="create-name" data-testid="label-player-name">Your Name</Label>
                <Input
                  id="create-name"
                  data-testid="input-player-name"
                  placeholder="Enter your name"
                  value={playerName}
                  onChange={(e) => setPlayerName(e.target.value)}
                  maxLength={20}
                />
              </div>

              <div>
                <Label htmlFor="max-players" data-testid="label-max-players">Maximum Players</Label>
                <select
                  id="max-players"
                  data-testid="select-max-players"
                  value={maxPlayers}
                  onChange={(e) => setMaxPlayers(parseInt(e.target.value))}
                  className="w-full mt-1 px-3 py-2 border border-gray-300 rounded-md bg-white"
                >
                  <option value={2}>2 Players</option>
                  <option value={3}>3 Players</option>
                  <option value={4}>4 Players</option>
                  <option value={5}>5 Players</option>
                  <option value={6}>6 Players</option>
                  <option value={7}>7 Players</option>
                  <option value={8}>8 Players</option>
                </select>
              </div>



              <div className="flex gap-2 pt-4">
                <Button
                  data-testid="button-create-confirm"
                  onClick={handleCreateRoom}
                  disabled={!isConnected}
                  className="bg-saffron hover:bg-orange-600 text-white flex-1"
                >
                  Create Room
                </Button>
                <Button
                  data-testid="button-create-cancel"
                  variant="outline"
                  onClick={() => setShowCreateForm(false)}
                  className="flex-1"
                >
                  Cancel
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Join Room Form */}
        {showJoinForm && (
          <Card className="mb-6">
            <CardHeader>
              <CardTitle className="text-2xl text-navy flex items-center">
                <i className="fas fa-sign-in-alt mr-2"></i>
                Join Existing Room
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="join-name" data-testid="label-join-name">Your Name</Label>
                <Input
                  id="join-name"
                  data-testid="input-join-name"
                  placeholder="Enter your name"
                  value={playerName}
                  onChange={(e) => setPlayerName(e.target.value)}
                  maxLength={20}
                />
              </div>

              <div>
                <Label htmlFor="room-code" data-testid="label-room-code">Room Code</Label>
                <Input
                  id="room-code"
                  data-testid="input-room-code"
                  placeholder="Enter 6-digit room code"
                  value={roomCode}
                  onChange={(e) => setRoomCode(e.target.value.toUpperCase())}
                  maxLength={6}
                />
              </div>



              <div className="flex gap-2 pt-4">
                <Button
                  data-testid="button-join-confirm"
                  onClick={handleJoinRoom}
                  disabled={!isConnected}
                  className="bg-indian-green hover:bg-green-700 text-white flex-1"
                >
                  Join Room
                </Button>
                <Button
                  data-testid="button-join-cancel"
                  variant="outline"
                  onClick={() => setShowJoinForm(false)}
                  className="flex-1"
                >
                  Cancel
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Connection Status */}
        <div className="text-center">
          <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm ${
            isConnected 
              ? "bg-green-100 text-green-800" 
              : "bg-red-100 text-red-800"
          }`}>
            <div className={`w-2 h-2 rounded-full mr-2 ${
              isConnected ? "bg-green-500" : "bg-red-500"
            }`} />
            {isConnected ? "Connected" : "Connecting..."}
          </div>
        </div>

        {/* Features showcase */}
        <div className="grid md:grid-cols-3 gap-6 mt-12">
          <div className="bg-white p-6 rounded-xl shadow-lg text-center">
            <i className="fas fa-map-marked-alt text-saffron text-3xl mb-4"></i>
            <h3 className="font-semibold text-lg mb-2">Indian Cities</h3>
            <p className="text-gray-600">Buy and trade famous Indian landmarks and cities</p>
          </div>
          <div className="bg-white p-6 rounded-xl shadow-lg text-center">
            <i className="fas fa-users text-indian-green text-3xl mb-4"></i>
            <h3 className="font-semibold text-lg mb-2">Multiplayer</h3>
            <p className="text-gray-600">Play with up to 8 friends in real-time</p>
          </div>
          <div className="bg-white p-6 rounded-xl shadow-lg text-center">
            <i className="fas fa-rupee-sign navy text-3xl mb-4"></i>
            <h3 className="font-semibold text-lg mb-2">Indian Currency</h3>
            <p className="text-gray-600">All transactions in Indian Rupees (₹)</p>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/Lobby.tsx.content:
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Label } from "@/components/ui/label";
import { useSocket } from "@/hooks/useSocket";
import { useToast } from "@/hooks/use-toast";
import { Room, Player } from "@shared/schema";

interface LobbyProps {
  roomCode: string;
}

export default function Lobby({ roomCode }: LobbyProps) {
  const [, setLocation] = useLocation();
  const [room, setRoom] = useState<Room | null>(null);
  const [currentPlayer, setCurrentPlayer] = useState<Player | null>(null);
  const [selectedColor, setSelectedColor] = useState("");
  const { socket, isConnected } = useSocket();
  const { toast } = useToast();

  const playerColors = [
    { name: "Red", value: "#EF4444", bg: "bg-red-500" },
    { name: "Blue", value: "#3B82F6", bg: "bg-blue-500" },
    { name: "Green", value: "#22C55E", bg: "bg-green-500" },
    { name: "Purple", value: "#A855F7", bg: "bg-purple-500" },
    { name: "Orange", value: "#F97316", bg: "bg-orange-500" },
    { name: "Pink", value: "#EC4899", bg: "bg-pink-500" },
    { name: "Cyan", value: "#06B6D4", bg: "bg-cyan-500" },
    { name: "Yellow", value: "#EAB308", bg: "bg-yellow-500" },
  ];

  useEffect(() => {
    if (!socket || !isConnected) return;

    const handleMessage = (event: MessageEvent) => {
      const data = JSON.parse(event.data);

      switch (data.type) {
        case 'roomJoined':
        case 'playerJoined':
        case 'playerReadyChanged':
        case 'playerDisconnected':
        case 'playerColorChanged':
        case 'playerKicked':
          setRoom(data.room);
          // Update current player if this is our player data
          const playerId = localStorage.getItem('playerId');
          if (playerId && data.room) {
            const player = data.room.players.find((p: Player) => p.id === playerId);
            if (player) {
              setCurrentPlayer(player);
              if (player.color && !selectedColor) {
                setSelectedColor(player.color);
              }
            }
          }
          break;

        case 'gameStarted':
          setLocation(`/game/${roomCode}`);
          break;

        case 'kicked':
          toast({
            title: "Kicked from Room",
            description: "You have been removed from the room by the host",
            variant: "destructive",
          });
          localStorage.removeItem('playerId');
          localStorage.removeItem('playerName');
          setTimeout(() => setLocation('/'), 2000);
          break;

        case 'error':
          toast({
            title: "Error",
            description: data.message,
            variant: "destructive",
          });
          // If room not found or can't join, redirect to home
          if (data.message === 'Room not found' || data.message === 'Failed to join room') {
            setTimeout(() => setLocation('/'), 2000);
          }
          break;
      }
    };

    socket.addEventListener('message', handleMessage);

    // Auto-join room if we have player data
    const playerId = localStorage.getItem('playerId');
    const playerName = localStorage.getItem('playerName');

    if (!playerId || !playerName) {
      // If no player data, redirect to home to create/join
      toast({
        title: "No Player Data",
        description: "Please create or join a room first",
        variant: "destructive",
      });
      setLocation('/');
      return;
    }

    // Try to rejoin the room
    socket.send(JSON.stringify({
      type: 'rejoinRoom',
      roomCode: roomCode,
      playerId: playerId
    }));

    return () => {
      socket.removeEventListener('message', handleMessage);
    };
  }, [socket, isConnected, roomCode, setLocation, toast]);

  // Helper functions for actions
  const handleColorChange = (color: string) => {
    if (!socket || !currentPlayer || selectedColor === color) return;

    socket.send(JSON.stringify({
      type: 'changeColor',
      playerId: currentPlayer.id,
      color: color
    }));
    setSelectedColor(color);
  };

  const handleKickPlayer = (playerToKickId: string) => {
    if (!socket || !currentPlayer) return;

    socket.send(JSON.stringify({
      type: 'kickPlayer',
      hostId: currentPlayer.id,
      playerToKickId: playerToKickId
    }));
  };

  // Get available colors (not already taken by other players)
  const getAvailableColors = () => {
    if (!room) return playerColors;

    const usedColors = room.players
      .filter(p => p.id !== currentPlayer?.id && p.color)
      .map(p => p.color);

    return playerColors.filter(color => !usedColors.includes(color.value));
  };

  useEffect(() => {
    const playerId = localStorage.getItem('playerId');
    if (room && playerId) {
      const player = room.players.find(p => p.id === playerId);
      setCurrentPlayer(player || null);
    }
  }, [room]);

  const handleToggleReady = () => {
    if (!socket || !currentPlayer) return;

    socket.send(JSON.stringify({
      type: 'playerReady',
      playerId: currentPlayer.id,
      isReady: !currentPlayer.isReady
    }));
  };

  const handleStartGame = () => {
    if (!socket || !currentPlayer) return;

    socket.send(JSON.stringify({
      type: 'startGame',
      hostId: currentPlayer.id
    }));
  };

  const isHost = currentPlayer && room && currentPlayer.id === room.hostId;
  const allReady = room?.players.every(p => p.isReady) ?? false;
  const minPlayers = (room?.players.length ?? 0) >= 2;

  if (!room) {
    return (
      <div className="min-h-screen bg-cream flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-saffron mb-4 mx-auto"></div>
          <p className="text-dark-slate">Loading lobby...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-cream">
      {/* Navigation Header */}
      <nav className="bg-navy text-white p-4 shadow-lg">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-2">
            <i className="fas fa-building text-saffron text-2xl"></i>
            <h1 className="text-2xl font-bold text-saffron">VYAPAAR</h1>
            <span className="text-sm opacity-75">Lobby</span>
          </div>
          <div className="flex items-center space-x-4">
            <span className="text-sm">
              Room: <span className="font-mono bg-saffron text-navy px-2 py-1 rounded" data-testid="text-room-code">{room.code}</span>
            </span>
            <Button
              variant="outline"
              size="sm"
              onClick={() => {
                const roomUrl = `${window.location.origin}/lobby/${room.code}`;
                navigator.clipboard.writeText(roomUrl);
                toast({
                  title: "Room Link Copied!",
                  description: "Share this link with other players",
                });
              }}
              data-testid="button-share-room"
              className="text-white border-white hover:bg-white hover:text-navy mr-2"
            >
              <i className="fas fa-share mr-1"></i>
              Share
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setLocation("/")}
              data-testid="button-leave-room"
              className="text-white border-white hover:bg-white hover:text-navy"
            >
              Leave Room
            </Button>
          </div>
        </div>
      </nav>

      <div className="container mx-auto px-4 py-6">
        <div className="max-w-4xl mx-auto">
          <Card className="mb-6">
            <CardHeader>
              <CardTitle className="text-2xl text-navy">Game Lobby</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid md:grid-cols-2 gap-6">
                {/* Players List */}
                <div>
                  <h3 className="font-semibold text-lg mb-4 text-dark-slate" data-testid="text-players-count">
                    Players ({room.players.length}/{room.maxPlayers})
                  </h3>
                  <div className="space-y-3">
                    {room.players.map((player, index) => (
                      <div key={player.id} className="flex items-center justify-between bg-gray-50 p-3 rounded-lg">
                        <div className="flex items-center space-x-3">
                          <div 
                            className={`w-10 h-10 rounded-full flex items-center justify-center text-white font-bold ${
                              !player.color ? 'bg-gray-400' : ''
                            }`}
                            style={{ backgroundColor: player.color || '#9CA3AF' }}
                            data-testid={`player-token-${player.name}`}
                          >
                            {player.avatar}
                          </div>
                          <div>
                            <div className="flex items-center space-x-2">
                              <span className="font-semibold" data-testid={`text-player-name-${player.name}`}>
                                {player.name}
                              </span>
                              {player.id === room.hostId && (
                                <Badge variant="secondary">Host</Badge>
                              )}
                              {player.id === currentPlayer?.id && (
                                <Badge variant="outline">You</Badge>
                              )}
                            </div>
                            <div className="text-sm text-gray-500">
                              {!player.isConnected ? "Disconnected" : 
                               !player.color ? "Choose a color" :
                               player.isReady ? "Ready to play!" : "Not ready"
                              }
                            </div>
                          </div>
                        </div>
                        <div className="flex items-center space-x-2">
                          {player.isConnected && player.color && (
                            <Badge
                              variant={player.isReady ? "default" : "outline"}
                              className={player.isReady ? "bg-green-100 text-green-800" : "bg-yellow-100 text-yellow-800"}
                              data-testid={`status-ready-${player.name}`}
                            >
                              {player.isReady ? "Ready" : "Not Ready"}
                            </Badge>
                          )}
                          {player.id === currentPlayer?.id && player.color && (
                            <Button
                              onClick={handleToggleReady}
                              size="sm"
                              variant={currentPlayer.isReady ? "outline" : "default"}
                              className={currentPlayer.isReady ? "hover:bg-red-50" : "bg-green-600 hover:bg-green-700"}
                              data-testid="button-toggle-ready"
                            >
                              {currentPlayer.isReady ? "Not Ready" : "Ready Up"}
                            </Button>
                          )}
                          {isHost && player.id !== currentPlayer?.id && (
                            <Button
                              onClick={() => handleKickPlayer(player.id)}
                              size="sm"
                              variant="destructive"
                              data-testid={`button-kick-${index}`}
                            >
                              Kick
                            </Button>
                          )}
                        </div>
                      </div>
                    ))}

                    {/* Empty slots */}
                    {Array.from({ length: room.maxPlayers - room.players.length }).map((_, index) => (
                      <div key={`empty-${index}`} className="flex items-center bg-gray-100 p-3 rounded-lg opacity-50">
                        <div className="w-10 h-10 bg-gray-300 rounded-full flex items-center justify-center">
                          <i className="fas fa-user-plus text-gray-500"></i>
                        </div>
                        <p className="ml-3 text-gray-500">Waiting for player...</p>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Color Selection & Game Controls */}
                <div>
                  <h3 className="font-semibold text-lg mb-4 text-dark-slate">Your Settings</h3>
                  <div className="space-y-4">
                    {/* Color Selection for Current Player */}
                    {currentPlayer && !currentPlayer.color && (
                      <div className="bg-white p-4 rounded-lg border-2 border-saffron">
                        <Label className="text-sm font-medium mb-3 block">Choose Your Color</Label>
                        <div className="grid grid-cols-4 gap-2">
                          {getAvailableColors().map((color) => (
                            <button
                              key={color.value}
                              onClick={() => handleColorChange(color.value)}
                              className={`w-12 h-12 rounded-full border-4 ${color.bg} transition-all hover:scale-110 ${
                                selectedColor === color.value
                                  ? "border-navy scale-110"
                                  : "border-gray-300 hover:border-gray-400"
                              }`}
                              title={color.name}
                              data-testid={`button-select-color-${color.name.toLowerCase()}`}
                            />
                          ))}
                        </div>
                        {getAvailableColors().length === 0 && (
                          <p className="text-sm text-gray-500 mt-2">No available colors. Please wait for other players to choose.</p>
                        )}
                      </div>
                    )}

                    {/* Game Settings Info */}
                    <div className="bg-gray-50 p-4 rounded-lg">
                      <p className="text-sm text-gray-600 mb-2">Room Code</p>
                      <p className="font-mono text-xl font-bold text-navy" data-testid="text-room-code-detail">{room.code}</p>
                    </div>
                    <div className="bg-gray-50 p-4 rounded-lg">
                      <p className="text-sm text-gray-600 mb-2">Starting Money</p>
                      <p className="font-semibold text-lg">₹15,000</p>
                    </div>
                    <div className="bg-gray-50 p-4 rounded-lg">
                      <p className="text-sm text-gray-600 mb-2">Max Players</p>
                      <p className="font-semibold text-lg">{room.maxPlayers} Players</p>
                    </div>
                    <div className="bg-gray-50 p-4 rounded-lg">
                      <p className="text-sm text-gray-600 mb-2">Game Status</p>
                      <Badge 
                        variant="outline" 
                        className="bg-yellow-100 text-yellow-800"
                        data-testid="status-game"
                      >
                        Waiting to Start
                      </Badge>
                    </div>
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="mt-6 text-center">
                <div className="flex justify-center gap-4">
                  {isHost ? (
                    <Button
                      onClick={handleStartGame}
                      disabled={!allReady || !minPlayers || !room.players.every(p => p.color)}
                      size="lg"
                      className="bg-saffron hover:bg-orange-600 text-white px-8"
                      data-testid="button-start-game"
                    >
                      <i className="fas fa-play mr-2"></i>
                      Start Game
                    </Button>
                  ) : (
                    currentPlayer && currentPlayer.color && (
                      <Button
                        onClick={handleToggleReady}
                        variant={currentPlayer.isReady ? "destructive" : "default"}
                        size="lg"
                        className={currentPlayer.isReady ? "" : "bg-green-600 hover:bg-green-700"}
                        data-testid="button-player-ready"
                      >
                        <i className={`fas ${currentPlayer.isReady ? 'fa-times' : 'fa-check'} mr-2`}></i>
                        {currentPlayer.isReady ? "Not Ready" : "Ready Up"}
                      </Button>
                    )
                  )}
                </div>

                {/* Status Messages */}
                <div className="mt-4 text-sm text-gray-600">
                  {isHost && !minPlayers && (
                    <p data-testid="message-min-players">Need at least 2 players to start</p>
                  )}
                  {isHost && minPlayers && !room.players.every(p => p.color) && (
                    <p data-testid="message-choose-colors">All players must choose colors first</p>
                  )}
                  {isHost && minPlayers && room.players.every(p => p.color) && !allReady && (
                    <p data-testid="message-waiting-players">Waiting for all players to be ready...</p>
                  )}
                  {!isHost && currentPlayer && !currentPlayer.color && (
                    <p data-testid="message-choose-color">Please choose your color first</p>
                  )}
                  {!isHost && currentPlayer && currentPlayer.color && (
                    <p data-testid="message-waiting-host">Waiting for host to start the game...</p>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Instructions */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg text-navy">How to Play</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid md:grid-cols-2 gap-6 text-sm">
                <div>
                  <h4 className="font-semibold mb-2">Getting Started</h4>
                  <ul className="space-y-1 text-gray-600">
                    <li>• Each player starts with ₹15,000</li>
                    <li>• All players begin at the START position</li>
                    <li>• Collect ₹2,000 when passing START</li>
                    <li>• Take turns rolling dice and moving</li>
                  </ul>
                </div>
                <div>
                  <h4 className="font-semibold mb-2">Winning Strategy</h4>
                  <ul className="space-y-1 text-gray-600">
                    <li>• Buy properties when you land on them</li>
                    <li>• Collect rent from other players</li>
                    <li>• Build houses and hotels for more rent</li>
                    <li>• Bankrupt your opponents to win!</li>
                  </ul>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

